actionConfiguration.body:
() => {
  login_api.run(() => {
    const jwt = login_api?.data?.access_token;
    if (jwt) {
      storeValue('jwt', jwt);
      get_userinfo.run(() => {
        storeValue('tabValue', 'BobData');
        resetWidget('LoginForm', true);
        navigateTo('Client Details', {});
        showAlert("Logged in successfully", 'success');
        fetch_customer.run(() => {}, () => {
          showAlert("Database connection timeout !!", 'error');
        });
      });
    }
  }, () => {
    showAlert('Incorrect Username/Password', 'error');
  });
}

